mapper.update:
update zp_apply_info
set work_status = #{isAgree}
where user_id = #{userId}
  and job_id = #{jobId}

mapper.select:
select company_id, job_name, job_type, link_man, phone_num, release_time, pay_way, job_require, (case when job_status = '0' then '未审批' when job_status = '1' then '已批准' when job_status = '2' then '已驳回' else job_status end ) as jobStatus, job_id from zp_work_info where 1=1
and company_id = #{companyId}
order by release_time desc

select *
from zp_apply_info
where user_id = #{userId}
  and job_id = #{jobId}

select a.company_id, a.job_name, a.job_type, a.link_man, a.phone_num, a.release_time, a.pay_way, a.job_require, (case when a.job_status = '0' then '未审批' when a.job_status = '1' then '已批准' when a.job_status = '2' then '已驳回' else a.job_status end ) as jobStatus, a.job_id, (case when b.work_status = '0' then '已报名' when b.work_status = '1' then '已被录用' when b.work_status = '2' then '已被拒绝' else '未报名' end ) as workStatus from zp_work_info a left join zp_apply_info b on a.job_id = b.job_id and b.user_id = #{userId} where a.job_status = '1'
and a.job_name like concat('%', #{params.jobName}, '%')
and a.pay_way = #{params.payWay}
and a.job_require like concat('%', #{params.sex}, '%')
and a.job_type =#{params.jobType}
order by a.release_time desc

select user_id, user_name, job_id, job_name, company_id, company_name, phone_num, (case when status = '0' then '待处理' when status = '1' then '已处理' else status end ) as status, content, feedback_time, deal_time, deal_content from zp_feedback_info where user_id = #{userId}
and status =#{params.backStatus}
order by feedback_time desc;

select user_id, user_name, job_id, job_name, company_id, company_name, phone_num, (case when status = '0' then '待处理' when status = '1' then '已处理' else status end ) as status, content, feedback_time, deal_time, deal_content from zp_feedback_info where 1=1
and company_id = #{companyId}
order by feedback_time desc
